import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/progresoBar.css';


const ProgresoBar = () => {
    const [progreso, setProgreso] = useState(0);

    useEffect(() => {
        const fetchProgreso = async () => {
            try {
                const response = await axios.get('http://localhost:8000/myapp/progreso/');
                console.log('Datos recibidos:', response.data); // Agrega esta línea para ver qué datos estás recibiendo
                setProgreso(response.data.progreso);
                ///console.log("progreso",response.data.progreso)
            } catch (error) {
                console.error("Error al calcular el progreso", error);
            }
        };

        fetchProgreso();
    }, []);

    return (
      <ProgressBar now={porcentaje} label={`${porcentaje}%`} />
    );
};

export default ProgresoBar;
