import React, { useState, useEffect } from 'react';
import '../../styles/1.css';
import { useNavigate } from 'react-router-dom';
import ProgresoBar from '../ProgresoBar'
import BarraSuperior from '../BarraSuperior'; // AsegÃºrate de la ruta correcta

const Uno = () => {
  const [draggedItem, setDraggedItem] = useState(null);
  const [droppedItem, setDroppedItem] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [showNextButton, setShowNextButton] = useState(false);
  const [score, setScore] = useState(0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleString());
  const navigate = useNavigate();

  const options = ['Mundo', 'Hola', 'Eduardo'];

  const handleDragStart = (e, item) => {
    setDraggedItem(item);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDroppedItem(draggedItem);
  };

  const handleVerify = () => {
    if (droppedItem === 'Mundo') {
      setIsCorrect(true);
      setShowNextButton(true);
      setScore(score + 10);

      // Reproducir el sonido de victoria
      const winningAudio = new Audio('/ganar.mp3'); // Ruta desde la carpeta public
      winningAudio.play().catch(error => console.error('Error al reproducir el audio:', error));
    } else {
      setIsCorrect(false);
      setShowNextButton(false);

      // Reproducir el sonido de derrota
      const losingAudio = new Audio('/perder.mp3'); // Ruta desde la carpeta public
      losingAudio.play().catch(error => console.error('Error al reproducir el audio:', error));
    }
  };

  const handleNext = () => {
    navigate('/enunciado3');
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTime(new Date().toLocaleString());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="nivel1-container">
      {/* SecciÃ³n de la barra lateral */}
      <div className="sidebar">
        <img src="tesis.png" alt="Logo" className="logo" />
        <button className="sidebar-button" onClick={() => navigate('/ejercicios1')}>
          <img src="flecha.png" alt="Inicio" className="sidebar-icon" />
          Atras
        </button>
        <button className="sidebar-button" onClick={() => navigate('/configuracion')}>
          <img src="configuracion.png" alt="ConfiguraciÃ³n" className="sidebar-icon" />
          CONFIGURACIÃN
        </button>
      </div>

      {/* Contenedor principal con el cuadro de informaciÃ³n y el contenido principal */}
      <div className="content">
        {/* Contenedor de informaciÃ³n sin GIF */}
        <BarraSuperior nombre={nombre} puntaje={puntaje} insignias={insignias} />

        {/* SecciÃ³n principal con el ejercicio */}
        <div className="white-background">
          <div className="header">
            <button className="icon-button">
              <img src="python1.png" alt="Icono Nivel" />
            </button>
            <div className="header-title">
              <h2>NIVEL 1</h2>
            </div>
            <div className="header-status">
              <span></span>
              <button className="icon-button">
                <img src="informacion.png" alt="Icono Moneda" />
              </button>
              <button className="icon-button" onClick={() => navigate('/dashboard')}>
                <img src="mapas.gif" alt="Icono Pregunta" />
              </button>
              <button className="icon-button">
                <img src="AYUDA.jpeg" alt="Icono Perfil" />
              </button>
            </div>
          </div>
          <div className="nivel1-card">

            <ProgresoBar/>
            </div>
          <div className={`nivel1-card`}>
            <div className="nivel1-card-header">
              <span>EJERCICIO #1</span>
            </div>
            <div className="nivel1-card-body">
              <p>
                A continuaciÃ³n, te presentamos nuestro primer ejercicio de nivel 1. El ejercicio consiste en identificar la palabra correcta en relaciÃ³n con el siguiente enunciado. Â¡Buena suerte!
                <br /><br />
                Por favor, arrastra la palabra que falta en el cÃ³digo para poder imprimir âHola, Mundo:â
              </p>
              <div className="code-box">
                <div className="code-header">Python</div>
                <div className="code-content">
                  <pre>
                    <code>
                      print("Hola, ________!")
                    </code>
                  </pre>
                </div>
              </div>
              <div className="drag-container">
                {options.map((option) => (
                  <div
                    key={option}
                    className="drag-option"
                    draggable
                    onDragStart={(e) => handleDragStart(e, option)}
                  >
                    {option}
                  </div>
                ))}
              </div>
              <div
                className="drop-zone"
                onDragOver={(e) => e.preventDefault()}
                onDrop={handleDrop}
              >
                {droppedItem ? `print("Hola, ${droppedItem}!")` : 'Arrastra aquÃ­ la palabra correcta'}
              </div>
              <div className="button-container">
                <button className="nivel1-card-button" onClick={handleVerify}>
                  Verificar
                </button>
                {showNextButton && (
                  <button className={`nivel1-card-button next-button show`} onClick={handleNext}>
                    Siguiente
                  </button>
                )}
              </div>
              <div className="result-container">
                {isCorrect !== null && (
                  <p className={`result ${isCorrect ? 'correct' : 'incorrect'}`}>
                    {isCorrect ? 'Â¡Correcto!' : 'IntÃ©ntalo de nuevo.'}
                  </p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Uno;
