import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/enunciado19.css'; // Asegúrate de que la ruta sea correcta

const Enunciado19 = () => {
    const [showGif, setShowGif] = useState(false);
    const [showContinue, setShowContinue] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const navigate = useNavigate(); // Hook para la redirección
    const [score, setScore] = useState(0); // Estado para el puntaje
    const [currentTime, setCurrentTime] = useState('');

    useEffect(() => {
        const updateTime = () => {
            const now = new Date();
            const formattedTime = `${now.toLocaleDateString()} ${now.toLocaleTimeString()}`;
            setCurrentTime(formattedTime);
            };
    
        updateTime();
        const intervalId = setInterval(updateTime, 1000); // Actualiza cada segundo
    
        return () => clearInterval(intervalId); // Limpia el intervalo al desmontar
      }, []);

    const handleShowGif = () => {
        setShowGif(true);
        setTimeout(() => {
        setShowContinue(true);
        }, 2000); // Asume que el GIF tiene una duración de 2 segundos
    };

    const handleContinueClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        navigate('/Ej19'); // Cambia la vista al siguiente componente
    };

    return (
        <div className="nivel1-container">
        <div className="sidebar">
            <img src="tesis.png" alt="Logo" className="logo" />
            <button className="sidebar-button" onClick={() => navigate('/Ej9')}>
            <img src="flecha.png" alt="Inicio" className="sidebar-icon" />
            Atrás
            </button>
            <button className="sidebar-button" onClick={() => navigate('/configuracion')}>
            <img src="configuracion.png" alt="Configuración" className="sidebar-icon" />
            CONFIGURACIÓN
            </button>
            <div className="score">
            {/* Aquí podrías añadir más elementos si los necesitas */}
            </div>
        </div>
        <div className="content">
            <div className="white-background">
            <div className="header">
                <button className="icon-button">
                <img src="python1.png" alt="Icono Nivel" />
                </button>
                <div className="header-title">
                <h2>NIVEL 1</h2>
                </div>
                <div className="header-status">
                <span></span>
                <button className="icon-button">
                    <img src="informacion.png" alt="Icono Moneda" />
                </button>
                <button className="icon-button" onClick={() => navigate('/dashboard')}>
                    <img src="ubicacion.png" alt="Icono Pregunta" />
                </button>
                <button className="icon-button">
                    <img src="AYUDA.jpeg" alt="Icono Perfil" />
                </button>
                </div>
            </div>
            <div className="nivel1-card">
                <div className="nivel1-card-header">
                <span>Conversión de Temperatura</span>
                </div>
                <div className="nivel1-card-body">
                <p>
                    En este ejercicio, aprenderás a convertir una temperatura de grados Celsius a Fahrenheit en Python.
                </p>
                <div className="code-box">
                    <div className="code-header">Python</div>
                    <pre className="code-content">
                    <code>
                    celsius = float(input("Ingresa la temperatura en grados Celsius: "))<br></br>
                    fahrenheit = (celsius * 9/5) + 32 <br></br>
                    print("La temperatura en Fahrenheit es:", fahrenheit)
                    </code>
                    </pre>
                </div>

                {!showGif && (
                    <div className="nivel1-card-button-container">
                    <button className="nivel1-card-button" onClick={handleShowGif}>
                        Ver Simulación
                    </button>
                    </div>
                )}

                {showGif && (
                    <div className="gif-container">
                    <img src="enunciado.gif" alt="GIF" className="gif-image" />
                    </div>
                )}

                {showContinue && (
                    <div className="nivel1-card-button-container">
                    <button className="nivel1-card-button" onClick={handleContinueClick}>
                        Continuar
                    </button>
                    </div>
                )}
                </div>
            </div>
            </div>
        </div>

        {showModal && (
            <div className="modal-overlay">
            <div className="modal-content">
                <h2>¡Sigue avanzando en Python!</h2>
                <p>
                🌟 ¡Fantástico! 🚀 Ahora has aprendido a convertir temperaturas en Python. Sigue practicando y mejorando tus habilidades. ¡Adelante, lo estás haciendo genial!
                </p>
                <img src="4.gif" alt="GIF de bienvenida" className="modal-gif" />
                <button className="modal-close-button" onClick={handleCloseModal}>
                Continuar
                </button>
            </div>
            </div>
        )}
        </div>
    );
};

export default Enunciado19;
