import React, { useState } from 'react';
import { GoogleOAuthProvider } from '@react-oauth/google';
import '../styles/Register.css';
import { useNavigate } from 'react-router-dom';
import axios from "axios";

const Register = ({ toggleView }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Hook para la redirección


  const handleRegister = async (e) => {
    e.preventDefault();
    if (!username || !email || !password) {
      alert('Por favor, completa todos los campos.');
      return;
    }
  
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(email)) {
      alert('Por favor, ingresa un correo electrónico válido.');
      return;
    }
  
    if (password.length <= 4) {
      alert('La contraseña debe tener al menos 4 caracteres.');
      return;
    }
    
    try {
      const response = await axios.post(
        "http://localhost:8000/myapp/register/",
        { email, username, password});

  
      //const data = await response.json();
      console.log('Success:', response.data);
      navigate('/login');
    } 

    catch (error) {
      console.error('Error:', error);
      alert('Ocurrió un error al registrar. Por favor, intenta de nuevo.');
    }
  };

  return (
    <div className="register-container">
      <img src="/logo.png" alt="Logo" className="register-logo" />
      <h2>Completa los siguientes espacios</h2>
      <form onSubmit={handleRegister} className="register-form">
        <div className="form-group">
          <input
            type="text"
            placeholder="Nombre y Apellido"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">REGISTRARME</button>
      </form>
      
      <button onClick={() => navigate('/login')} className="toggle-button">Iniciar Sesión</button>
      <p className="terms">
        Al registrarte en XXX, aceptas nuestros <a href="">Términos y Política de privacidad</a>.
      </p>
    </div>
  );
};

export default Register;
