import React, { useState, useEffect  } from 'react';
import '../styles/foro.css';
import { useNavigate } from 'react-router-dom';
import axios from "axios"

const Foro = ({ toggleView }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isResponseModalOpen, setIsResponseModalOpen] = useState(false);
    const [question, setQuestion] = useState("");
    const [responseUsername, setResponseUsername] = useState("");
    const [responseText, setResponseText] = useState("");
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [tema, setTema] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fecha_creacion, setFecha_creacion] = useState('');
    const [questions, setQuestions] = useState([]);
    const [successMessage, setSuccessMessage] = useState("");
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(null);
    const [searchText, setSearchText] = useState("");

    const fetchQuestions = async () => {
        try {
            const response = await axios.get('http://localhost:8000/myapp/registroForo/');
            console.log("response", response);
            if (Array.isArray(response.data)) {
                setQuestions(response.data); // Asigna el array directamente
            } else {
                console.error('La respuesta de la API no es un array:', response.data);
            }
        } catch (error) {
            console.error('Error obteniendo los foros:', error);
        }
    };
    useEffect(() => {
        fetchQuestions();
    }, []);

    const handleBackClick = () => {
        navigate("/dashboard");
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setIsResponseModalOpen(false);
    };

    const handleSubmit = async () => {
        if (username && question && tema && descripcion && fecha_creacion) {
            setQuestions([
                ...questions,
                {
                    user: username,
                    time:
                        new Date().toLocaleTimeString([], {
                            hour: "2-digit",
                            minute: "2-digit",
                        }) +
                        " " +
                        new Date().toLocaleDateString(),
                    text: question,
                    answers: [],
                },
            ]);
            
            try {
                await handleRegistroForo();

                setUsername("");
                setQuestion("");
                setTema("");
                setDescripcion("");
                setFecha_creacion("");
                setSuccessMessage("¡Pregunta publicada y foro registrado exitosamente!");
                setTimeout(() => setSuccessMessage(""), 3000);
                closeModal();

            } catch (error) {
                console.error('Error registrando el foro:', error);
            }
        } else {
            alert("Por favor, completa todos los campos.");
        }
    };

    const handleRegistroForo = async () => {
        try {
            const response = await axios.post(
                "http://localhost:8000/myapp/registroForo/",
                { tema, descripcion, fecha_creacion }
            );

            console.log('Success:', response.data);
        } catch (error) {
            console.error('Error:', error);
            alert('Ocurrió un error al registrar el foro. Por favor, intenta de nuevo.');
        }
    };
    const handleRegistroRespuestaForo = async () => {
        try {
            const response = await axios.post(
                "http://localhost:8000/myapp/registroParti_foro/",
                { 
                    usuario_id: responseUsername,
                    foro_id: questions[currentQuestionIndex].id,  // Asegúrate de que el foro_id esté disponible en tu estructura de datos
                    fecha_participacion: new Date().toISOString().split('T')[0], 
                    comentario: responseText,
                    resultado: true 
                }
            );

            console.log('Success:', response.data);
        } catch (error) {
            console.error('Error registrando la respuesta:', error);
            alert('Ocurrió un error al registrar la respuesta. Por favor, intenta de nuevo.');
        }
    };


    const openResponseModal = (index) => {
        setCurrentQuestionIndex(index);
        setIsResponseModalOpen(true);
    };

    const handleResponseSubmit = async () => {
        if (responseUsername && responseText && currentQuestionIndex !== null) {
            const updatedQuestions = [...questions];
            updatedQuestions[currentQuestionIndex].answers.push({
                user: responseUsername,
                time:
                    new Date().toLocaleTimeString([], {
                        hour: "2-digit",
                        minute: "2-digit",
                    }) +
                    " " +
                    new Date().toLocaleDateString(),
                text: responseText,
            });

            try {
                await handleRegistroRespuestaForo(updatedQuestions[currentQuestionIndex].id);

                setQuestions(updatedQuestions);
                setResponseUsername("");
                setResponseText("");
                setSuccessMessage("¡Respuesta publicada exitosamente!");
                setTimeout(() => setSuccessMessage(""), 3000);
                closeModal();
            } catch (error) {
                console.error('Error registrando la respuesta:', error);
            }
        } else {
            alert("Por favor, completa todos los campos.");
        }
    };

    const handleDelete = (index) => {
        setQuestions(questions.filter((_, i) => i !== index));
    };

    const handleSearchChange = (e) => {
        setSearchText(e.target.value);
    };

    const filteredQuestions = questions.filter((q) => {
        console.log("Questions:", questions);
        const userMatch = q.user?.toLowerCase().includes(searchText.toLowerCase());
        const textMatch = q.text?.toLowerCase().includes(searchText.toLowerCase());
        const answerMatch = q.answers?.some((a) =>
            a.text?.toLowerCase().includes(searchText.toLowerCase())
        );
    
        return userMatch || textMatch || answerMatch;
    });

    return (
        <div className="foro-container">
            <div className="foro-header">
                <button onClick={handleBackClick} className="back-button">
                    <img src="atrasa.png" alt="Back" className="back-icon" />
                </button>
                <input
                    type="text"
                    placeholder="Buscar por usuario o palabra..."
                    className="search-bar"
                    value={searchText}
                    onChange={handleSearchChange}
                />
                <div className="icon-group">
                    <img src="ubicacion.png" alt="Icon 1" className="header-icon" />
                    <img src="informacion.png" alt="Icon 2" className="header-icon" />
                    <img src="mensaje.png" alt="Icon 3" className="header-icon" />
                    <img src="AYUDA.jpeg" alt="Profile" className="header-icon" />
                </div>
            </div>
            <div className="foro-content">
                <div className="foro-subheader">
                    <button className="new-question" onClick={openModal}>
                        Nueva Pregunta
                    </button>
                </div>
                <div className="foro-title">
                    Únete a la conversación en nuestro foro
                </div>
                {successMessage && (
                    <div className="success-message">{successMessage}</div>
                )}
                <div className="foro-questions">
                    {filteredQuestions.map((q, index) => (
                        <div className="question" key={index}>
                            <div className="question-header">
                                <p className="question-info">
                                    {q.user} Hora {q.time}
                                </p>
                            </div>
                            <p className="question-text">{q.text}</p>
                            <div className="answers">
                                {q.answers.map((a, idx) => (
                                    <div className="answer" key={idx}>
                                        <div className="answer-header">
                                            <p className="answer-info">
                                                Respuesta: {a.user} Hora {a.time}
                                            </p>
                                        </div>
                                        <p className="answer-text">{a.text}</p>
                                    </div>
                                ))}
                            </div>
                            <div className="actions">
                                <div className="like-dislike">
                                    <img src="like.png" alt="Like" className="like-icon" />
                                    <img
                                        src="nomegusta.png"
                                        alt="Dislike"
                                        className="dislike-icon"
                                    />
                                </div>
                                <div className="additional-actions">
                                    <img
                                        src="eliminar.png"
                                        alt="Eliminar"
                                        className="action-icon"
                                        onClick={() => handleDelete(index)}
                                    />
                                    <img
                                        src="correo.png"
                                        alt="Correo"
                                        className="action-icon"
                                        onClick={() => openResponseModal(index)}
                                    />
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>Publicar Nueva Pregunta</h2>
                        <input
                            type="text"
                            placeholder="Nombre de Usuario"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="modal-input"
                        />
                        <textarea
                            placeholder="Descripción de la Pregunta"
                            value={question}
                            onChange={(e) => setQuestion(e.target.value)}
                            className="modal-textarea"
                        />
                        <input
                            type="text"
                            placeholder="Tema"
                            value={tema}
                            onChange={(e) => setTema(e.target.value)}
                            className="modal-input"
                        />
                        <textarea
                            placeholder="Descripción del Foro"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            className="modal-textarea"
                        />
                        <input
                            type="date"
                            value={fecha_creacion}
                            onChange={(e) => setFecha_creacion(e.target.value)}
                            className="modal-input"
                        />
                        <button onClick={handleSubmit} className="modal-button">
                            Publicar
                        </button>
                        <button onClick={closeModal} className="modal-close">
                            Cerrar
                        </button>
                    </div>
                </div>
            )}

            {isResponseModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>Responder Pregunta</h2>
                        <input
                            type="text"
                            placeholder="Nombre de Usuario"
                            value={responseUsername}
                            onChange={(e) => setResponseUsername(e.target.value)}
                            className="modal-input"
                        />
                        <textarea
                            placeholder="Descripción de la Respuesta"
                            value={responseText}
                            onChange={(e) => setResponseText(e.target.value)}
                            className="modal-textarea"
                        />
                        <button
                            onClick={handleResponseSubmit}
                            className="modal-button response-button"
                        >
                            Publicar Respuesta
                        </button>
                        <button onClick={closeModal} className="modal-close">
                            Cerrar
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Foro;
