import React, { useState } from 'react';
import axios from 'axios';

const EditorCodigo = () => {
    const [codigo, setCodigo] = useState('');
    const [output, setOutput] = useState('');
    const [error, setError] = useState('');

    const handleRunCode = async () => {
        try {
            const codigo = "print('hola mundo')";
            const response = await axios.post('http://localhost:8000/myapp/run_code/', { codigo });
            
            // Verifica que `output` exista en la respuesta
            if (response.data && response.data.output) {
                setOutput(response.data.output);
                setError('');
            } else {
                setOutput('');
                setError('No se recibió la salida esperada.');
            }
        } catch (err) {
            // Verifica si `err.response` y `err.response.data` existen antes de acceder a `error`
            if (err.response && err.response.data && err.response.data.error) {
                setError(err.response.data.error);
            } else {
                setError('Ha ocurrido un error');
            }
            setOutput('');
        }
    };
    

    return (
        <div>
            
            <button onClick={handleRunCode}>Run Codigo</button>
            {output && <pre>Output: {output}</pre>}
            {error && <pre style={{ color: 'red' }}>Error: {error}</pre>}

        </div>
    );
};

export default EditorCodigo;
